mapper.select:
select ps.*,ui.ui_id from PPG_STATS ps inner join USER_INFO ui on ps.UI_NUM=ui.UI_NUM


		order by ps_num desc


		order by ${order}


select count(1) as cnt, credat from ppg_stats group by credat


			order by credat desc


			order by ${order}

select count(1) as cnt, ps.ui_num, ui.ui_id from ppg_stats ps join user_info ui on ps.ui_num=ui.ui_num group by ps.ui_num, ui.ui_id
order by cnt desc
order by ${order}
